SELECT [Time Dimension tbl].Date, [Time Dimension tbl].[Calendar Year], [Time Dimension tbl].[Week Ending Sunday], [BrandIndex_Index tbl].Product, [BrandIndex_Index tbl].[Index Classification], [BrandIndex_Index tbl].Standard AS NumStandard, [BrandIndex_Index tbl].Proportional AS NumProportional FROM [Time Dimension tbl] LEFT JOIN [BrandIndex_Index tbl] ON [Time Dimension tbl].Date = [BrandIndex_Index tbl].Date;
