SELECT [DDS_Lookup Monday to Sunday].[Week Ending Sunday], [DDS Hispanic TV by Length tbl].[ACT WM3564 GRP], [DDS Hispanic TV by Length tbl].[ACT WM2554 GRP], [DDS Hispanic TV by Length tbl].[ACT HOMES GRP], [DDS Hispanic TV by Length tbl].Spend, [DDS Hispanic TV by Length tbl].Prd, [DDS_Lookup TV Time Length].Len FROM ([DDS_Lookup Monday to Sunday] LEFT JOIN [DDS Hispanic TV by Length tbl] ON [DDS_Lookup Monday to Sunday].[Week Starting Monday] = [DDS Hispanic TV by Length tbl].[Week Starting Monday]) LEFT JOIN [DDS_Lookup TV Time Length] ON [DDS Hispanic TV by Length tbl].LEN = [DDS_Lookup TV Time Length].Time;
