SELECT [DDS_Lookup Monday to Sunday].[Week Ending Sunday], [DDS_Lookup TV Time Length].Len, [DDS Spot TV tbl].Product, [DDS Spot TV tbl].Program, [DDS Spot TV tbl].[PURCH RHOMES], [DDS Spot TV tbl]![PURCH RHOMES]*[DMA to IRI Nat+Region wgts]![National Weight] AS [Normalized HH GRP], [DDS Spot TV tbl].[PURCH DOLLARS], [DDS Spot TV tbl].Market FROM (([DDS_Lookup Monday to Sunday] LEFT JOIN [DDS Spot TV tbl] ON [DDS_Lookup Monday to Sunday].[Week Starting Monday] = [DDS Spot TV tbl].Week) LEFT JOIN [DMA to IRI Nat+Region wgts] ON [DDS Spot TV tbl].Market = [DMA to IRI Nat+Region wgts].[DDS Spot TV Market]) LEFT JOIN [DDS_Lookup TV Time Length] ON [DDS Spot TV tbl].Len = [DDS_Lookup TV Time Length].Time;
