SELECT [DDS_Lookup Monday to Sunday].[Week Ending Sunday], [DDS/Mbox TV Media Type tbl].MEDIA, [DDS/Mbox TV Media Type tbl].[ACT WM3564 GRP], [DDS/Mbox TV Media Type tbl].[ACT WM2554 GRP], [DDS/Mbox TV Media Type tbl].[ACT HOMES GRP], [DDS/Mbox TV Media Type tbl].Spend, [DDS/Mbox TV Media Type tbl].Product FROM [DDS_Lookup Monday to Sunday] LEFT JOIN [DDS/Mbox TV Media Type tbl] ON [DDS_Lookup Monday to Sunday].[Week Starting Monday] = [DDS/Mbox TV Media Type tbl].Date;
