SELECT [Time_Lookup_Day to Week Ending Sunday].[Week Ending Sunday], [Kantar_Lookup_Brand tbl].Brand, Sum([Kantar_Print tbl]![Dols (000)]*1000) AS [Media Spend], Sum([Kantar_Print tbl].[P 18+ GRP]) AS [SumOfP 18+ GRP], [Kantar_Print tbl].Product, [Kantar_Lookup_Brand tbl].[Kantar Product] FROM [Time_Lookup_Day to Week Ending Sunday] LEFT JOIN ([Kantar_Print tbl] LEFT JOIN [Kantar_Lookup_Brand tbl] ON [Kantar_Print tbl].Product = [Kantar_Lookup_Brand tbl].[Kantar Product]) ON [Time_Lookup_Day to Week Ending Sunday].Date = [Kantar_Print tbl].Date GROUP BY [Time_Lookup_Day to Week Ending Sunday].[Week Ending Sunday], [Kantar_Lookup_Brand tbl].Brand, [Kantar_Print tbl].Product, [Kantar_Lookup_Brand tbl].[Kantar Product];
