SELECT [Kantar_Lookup_Date tbl].[Week Ending Sunday], [Kantar_Lookup_Brand tbl].Brand, Sum([Kantar_Print OOH tbl].[OUTDOOR DOLS (000)]) AS [SumOfOUTDOOR DOLS (000)], [Kantar_Print OOH tbl].[MARKET] FROM ([Kantar_Lookup_Date tbl] LEFT JOIN [Kantar_Print OOH tbl] ON [Kantar_Lookup_Date tbl].[Kantar Date] = [Kantar_Print OOH tbl].[TIME PERIOD]) LEFT JOIN [Kantar_Lookup_Brand tbl] ON [Kantar_Print OOH tbl].PRODUCT = [Kantar_Lookup_Brand tbl].[Kantar Product] GROUP BY [Kantar_Lookup_Date tbl].[Week Ending Sunday], [Kantar_Lookup_Brand tbl].Brand, [Kantar_Print OOH tbl].[MARKET];
