SELECT [SDI_Lookup_Wk end SAT FRI to Wk end Sunday].[Week End Sunday], [SDI Data Tbl].[Week Ending], Sum([SDI Data Tbl].[Projected Affected Population]) AS SumOfAffectedPopulation, [SDI Data Tbl].Status, [SDI Data Tbl].State, [SDI Data Tbl].Type, [SDI Data Tbl].City FROM [SDI_Lookup_Wk end SAT FRI to Wk end Sunday] LEFT JOIN [SDI Data Tbl] ON [SDI_Lookup_Wk end SAT FRI to Wk end Sunday].[Week end Saturday] = [SDI Data Tbl].[Week Ending] GROUP BY [SDI Data Tbl].Status, [SDI Data Tbl].State, [SDI Data Tbl].Type, [SDI Data Tbl].City, [SDI Data Tbl].[Week Ending], [SDI_Lookup_Wk end SAT FRI to Wk end Sunday].[Week End Sunday];
